const cards=document.querySelectorAll(".slider-card"),sliderItemsBlock=document.querySelector(".slider-wrapper");let width,count=0;const rollSlider=t=>{sliderItemsBlock.style.transform=`translate(-${t*width}px)`},calculateSliderWidth=()=>{width=document.querySelector(".slider").offsetWidth,sliderItemsBlock.style.width=width*cards.length+"px",cards.forEach(t=>{t.style.maxWidth="960px",t.style.width=width+"px"})},calculateWidth=t=>{sliderItemsBlock.style.width="100%",cards.forEach(e=>{e.style.maxWidth=t+"px",e.style.width="100%"})},changeActivePaginationItem=()=>{paginationItem.forEach(t=>{t.id==count?t.classList.add("slider-pagination__item_active"):t.classList.remove("slider-pagination__item_active")})};document.documentElement.clientWidth>=768&&document.documentElement.clientWidth<1024&&calculateSliderWidth(),window.addEventListener("resize",()=>{document.documentElement.clientWidth>=768&&document.documentElement.clientWidth<1024?calculateSliderWidth():document.documentElement.clientWidth>=1024?(calculateWidth(390),rollSlider(0)):document.documentElement.clientWidth<768&&(calculateWidth(960),rollSlider(0))}),nextBtn.addEventListener("click",()=>{count++,count>=cards.length&&(count=0),changeActivePaginationItem(),rollSlider(count)}),prevBtn.addEventListener("click",()=>{count--,count<0&&(count=cards.length-1),changeActivePaginationItem(),rollSlider(count)}),paginationBlock.addEventListener("click",t=>{t.target.classList.contains("slider-pagination__item")&&(paginationItem.forEach(e=>{t.target.id==e.id?(count=e.id,e.classList.add("slider-pagination__item_active")):e.classList.remove("slider-pagination__item_active")}),rollSlider(count))});const cardItems=document.querySelectorAll(".section__item");showAllBtn.addEventListener("click",()=>{cardItems.forEach(t=>t.style.display="block"),showAllBtn.style.display="none"});